// this utility is hosted elsewhere and included here to avoid unnecessary dependencies

/**
 * Extension of util.inherits that inherits also static methods.
 * 
 * @param ctor Constructor of a newly defined class
 * @param superCtor Constructor of the parent class
 */
var inherit = function(ctor, superCtor) {
    /* copy statics */
    for (var key in superCtor) {
        if (superCtor.hasOwnProperty(key) && !ctor.hasOwnProperty(key)) {
            ctor[key] = superCtor[key];
        }
    }

    /* memorise super constructor */
    ctor.super_ = superCtor;
    
    /* derive prototype */
    ctor.prototype = Object.create(superCtor.prototype, {
        constructor : {
            value : ctor,
            enumerable : false,
            writable : true,
            configurable : true
        }
    });
};


module.exports = {
    inherit: inherit
};
